import { Container, Row, Col } from 'react-bootstrap';
import './TodoList.css';
import TodoItem from '../TodoItem/TodoItem';
import { useState } from 'react';

function TodoList() {

    // THE STATES USED

    const [todoItems, setTodoItems] = useState([]);
    const [todoText, setTodoText] = useState([]);

    // THE FUNCTIONS USED
    const onAddTodo = ()=>{
        setTodoItems([
            ...todoItems,
            {
                id: todoItems.length+1,
                text: todoText
            }
        ])

        setTodoText('');
    }

    const onDeleteTodo = (id)=>{
        const updatedTodos = todoItems.filter((todoitem) => (todoitem.id !== id))
        setTodoItems(updatedTodos);
    }

    return (
        <Container fluid>
            <Row>
                <Col>
                    <div className='header'>

                        {/* Todo list header containing Title, mod-img and an input-field */}
                        <div className='header-title'>
                            <h1>TODO</h1>
                            <img src="../Assests/icon-sun.svg" alt="" />
                        </div>

                        <div className='input-todo-box'>
                            <input type="text" placeholder='Create a new todo...' value={todoText} onChange={(e) =>{setTodoText(e.target.value)}}/>
                            <img src="../Assests/add.png" alt="" onClick={onAddTodo}/>
                        </div>

                    </div>

                    <div className='main-list'>
                        {todoItems.map((todoitem) =>(
                            <TodoItem key={todoitem.id} todo={todoitem} text={todoitem.text} onDelete={onDeleteTodo}/>
                        ))}
                    </div>
                </Col>
            </Row>
        </Container>
    );
}

export default TodoList;
